import React, { useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

function App() {
  const [recipientEmail, setRecipientEmail] = useState("");
  const [prompt, setPrompt] = useState("");
  const [emailText, setEmailText] = useState("");
  const [showSend, setShowSend] = useState(false);

  // const BACKEND_URL = "https://email-generation-backend-8pmu.onrender.com";

  const BACKEND_URL = "https://localhost:5000";

  const handleGenerate = async () => {
    console.log("Generating email...");
    console.log("Recipient:", recipientEmail);
    console.log("Prompt:", prompt);

    try {
      const res = await fetch(`${BACKEND_URL}/api/generate-email`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ recipient: recipientEmail, prompt }),
      });

      const data = await res.json();
      console.log("Generated Email:", data.email);

      setEmailText(data.email);
      setShowSend(true);
    } catch (error) {
      console.error("Error generating email:", error);
      alert("Failed to generate email.");
    }
  };

  const handleSend = async () => {
    console.log("Sending email...");
    console.log("To:", recipientEmail);
    console.log("Subject: Generated Email");
    console.log("Text:", emailText);

    try {
      const res = await fetch(`${BACKEND_URL}/api/send-email`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          to: recipientEmail,
          subject: "Generated Email",
          text: emailText,
        }),
      });

      if (res.ok) {
        console.log("Email sent successfully!");
        alert("Email sent successfully!");
        setShowSend(false);
      } else {
        const errorText = await res.text();
        console.error("Failed to send email:", errorText);
        alert("Failed to send email.");
      }
    } catch (error) {
      console.error("Error sending email:", error);
      alert("Error occurred while sending email.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Email Generator</h1>

      <input
        type="email"
        placeholder="Recipient Email"
        value={recipientEmail}
        onChange={(e) => setRecipientEmail(e.target.value)}
        style={{ width: "300px", marginBottom: "10px", display: "block" }}
      />

      <input
        type="text"
        placeholder="Prompt"
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        style={{ width: "300px", marginBottom: "10px", display: "block" }}
      />

      <button onClick={handleGenerate}>Generate Email</button>

      <div style={{ marginTop: "20px" }}>
        <ReactQuill value={emailText} onChange={setEmailText} />
      </div>

      {showSend && (
        <button onClick={handleSend} style={{ marginTop: "20px" }}>
          Send Email
        </button>
      )}
    </div>
  );
}

export default App;
